package week1.day1;

public class OddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Print Only Odd Numbers");

		for (int i = 1; i <20 ; i++) 
		{
			if (i%2!=0)
			
			{
			System.out.println(i);	
			}
		}
		
	}

}
